import React from 'react';
import { motion } from "framer-motion";
import { personalData } from '../../data/personal';
import SectionTitle from '../../components/SectionTitle/SectionTitle';
import './Skills.css';

// Skill emoji mapping
const skillEmojis = {
  // Languages
  "Python": "ðŸ",
  "Java": "â˜•",
  "JavaScript": "ðŸ“œ",
  "HTML": "ðŸŒ",
  "CSS": "ðŸŽ¨",

  // Frameworks
  "Flask": "ðŸŒ¶ï¸",
  "FastAPI": "âš¡",
  "Django": "ðŸŽ¸",
  "React.js": "âš›ï¸",

  // Databases
  "MySQL": "ðŸ—„ï¸",
  "PostgreSQL": "ðŸ˜",
  "MongoDB": "ðŸƒ",

  // Cloud
  "AWS Lambda": "Î»",
  "EC2": "â˜ï¸",
  "DynamoDB": "âš¡",

  // CI/CD & DevOps
  "Docker": "ðŸ³",
  "Git": "ðŸ“¦",
  "GitHub Actions": "âš™ï¸",

  // Testing
  "Pytest": "ðŸ§ª",
  "Unittest": "ðŸ”¬",

  // Machine Learning
  "Logistic Regression": "ðŸ“Š",
  "Linear Regression": "ðŸ“ˆ",
  "Random Forest": "ðŸŒ²",
  "Decision Tree": "ðŸŒ³",
  "KNN": "ðŸ”",

  // LLM & GenAI
  "RAG": "ðŸ¤–",
  "HuggingFace": "ðŸ¤—",
};

const getSkillEmoji = (skillName) => {
  return skillEmojis[skillName] || "ðŸ’¼";
};

const Skills = () => {
  return (
    <section className="skills" id="skills">
      <div className="skills-container">
        <SectionTitle title="Skills" subtitle="Technologies I work with" />
        <div className="skills-carousel-wrapper">
          {personalData.skills.map((section, idx) => (
            <div key={idx} className="skill-section">
              <motion.h2 
                className="skill-category-title"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                {section.category}
              </motion.h2>
              <div className="skill-carousel-container">
                <div className="skill-carousel-wrapper-inner">
                  <motion.div
                    className="skill-carousel"
                    animate={{ 
                      x: ["0%", "-33.333%"]
                    }}
                    transition={{
                      repeat: Infinity,
                      duration: 20,
                      ease: "linear",
                      repeatType: "loop",
                    }}
                  >
                    {[...section.items, ...section.items, ...section.items].map((skill, index) => (
                      <motion.div
                        key={`${section.category}-${index}`}
                        whileHover={{ scale: 1.15 }}
                        className="skill-badge"
                      >
                        {skill}
                      </motion.div>
                    ))}
                  </motion.div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
